import axios from 'axios'
import Home from "./Home"
import { useState, useEffect, useRef } from 'react'
import { Link } from 'react-router-dom'
import EditProductDetails from './editProductDetails'
export default function Productlist() {
    let [productlist, setProducts] = useState([]);
    let [status, setStatus] = useState(false)
    let [state,setState]  = useState([]);

    let productName = useRef(null);
    let productBrand = useRef(null);
    let productImg = useRef(null);
    let productPrice = useRef(null);
    let productDescription = useRef(null);
    let newIndex;
    useEffect(() => {
        axios.get("https://dummyjson.com/products").then(response => {
            setProducts(response.data.products)
            console.log(productlist)
        }).catch(err => {
            console.log(err)
        })
    }, [])
    let allProduct = productlist;
    const updateStatus = () => {
        let editedForm = document.getElementById("editedForm")
        editedForm.style.display = 'none'
        let Table = document.getElementById("table");
        Table.style.display = 'block'
        setStatus(true);
    }
    const deleteProduct = (index) => {
        productlist.splice(index, 1);
        let arr = [...productlist];
        setProducts(arr)
    }
    const updateDetail = ()=>{
        let arr = [...productlist];
        let name = productName.current.value;
        productlist[newIndex].title = name;
        let brand = productBrand.current.value;
        productlist[newIndex].brand = brand;
        let img = productImg.current.value;
        productlist[newIndex].thumbnail = img;
        let price = parseFloat(productPrice.current.value);
        productlist[newIndex].price = price;
        let description = productDescription.current.value;
        productlist[newIndex].description = description;
        setProducts(arr)
    }
    const editProductDetail = (data) => {
        setState(data)
        let editedForm = document.getElementById("editedForm")
        editedForm.style.display = 'block'
        let Table = document.getElementById("table");
        Table.style.display = 'none'
        let index = productlist.findIndex(product => product.title === data.title);
        newIndex = index
    }



    return <>
        <Home />
        <div className='container-fluid' id='table'>
            <h1 className='text-center'>ProductList</h1>
            <div className='container'>
                <table className='table'>
                    <thead>
                        <th>Sr. No.</th>
                        <th>Product Name</th>
                        <th>Product Brand</th>
                        <th>Product Image</th>
                        <th>Product Price</th>
                        <th>Delete</th>
                        <th>Edit</th>
                    </thead>
                    {productlist.map((data, index) => <tr key={index}>
                        <td>{index + 1}</td>
                        <td>{data.title}</td>
                        <td>{data.brand}</td>
                        <td><img src={data.thumbnail} width='100px' height='80px' alt='product' /></td>
                        <td>{data.price} â‚¹</td>
                        <td><button onClick={() => deleteProduct(index)} className='btn btn-outline-danger'>Delete</button></td>
                        <td><button onClick={()=>editProductDetail({...data})} className='btn btn-outline-primary'>Edit</button></td>
                    </tr>)}
                </table>
            </div>
        </div>


        <div className="container border mt-2" id='editedForm'>
            <h3 className="text-center mt-2">Edit Product Details</h3>
            <div className="form-group p-2">
                <input ref={productName} className="form-control m-2" placeholder="product name" defaultValue={state.title} />
                <input ref={productBrand} className="form-control m-2" placeholder="product brand " defaultValue={state.brand}/>
                <input ref={productImg} type="file" className="form-control m-2 p-1" placeholder="product image " defaultValue={state.img}/>
                <input ref={productPrice} className="form-control m-2" placeholder="product Price " defaultValue={state.price}/>
                <input ref={productDescription} className="form-control m-2" placeholder="product Description " defaultValue={state.description}/>
                <button onClick={updateStatus} className="btn btn-success m-2">Submit</button>
            </div>
        </div>

        ({status && <EditProductDetails allProduct={allProduct} />})
    </>
}
