import axios from 'axios'
import Home from "./Home"
import {useState,useEffect,useRef} from 'react'
import { Link } from 'react-router-dom'
import EditProductDetails from './editProductDetails'
export default function Productlist(){
    let [productlist,setProducts] = useState([]);
    let [status,setStatus] = useState(false)
    useEffect(()=>{
        axios.get("https://dummyjson.com/products").then(response=>{
            setProducts(response.data.products)
            console.log(productlist)
        }).catch(err=>{
            console.log(err)
        })   
    },[])
    let allProduct = productlist;
    const updateStatus = ()=>{
        setStatus(true);
    }
    const deleteProduct = (index)=>{
        productlist.splice(index,1);
        let arr = [...productlist];
        setProducts(arr)
    }
    const editProductDetail = (data)=>{
        let index = productlist.findIndex(product=>product.title===data.title);
        productlist[index].title = 'mobile'
        let arr = [...productlist];
        setProducts(arr)
    }



    let productName = useRef(null);
    let productBrand = useRef(null);
    let productImg = useRef(null);
    let productPrice = useRef(null);
    let productDescription = useRef(null);
    const updateValues = ()=>{
        let name = productName.current.value;
        let  brand = productBrand.current.value;
        let  img = productImg.current.value;
        console.log(productImg)
        let price = parseFloat(productPrice.current.value);
        let  description= productDescription.current.value;
        window.alert(name+" "+brand+"  "+img+" "+price+description)
    }
    
    <div className="container border mt-2">
        <h3 className="text-center mt-2">Edit Product Details</h3>
        <div className="form-group p-2">
            <input ref={productName} className="form-control m-2" placeholder="product name "/>
            <input ref={productBrand} className="form-control m-2" placeholder="product brand "/>
            <input ref={productImg} type="file" className="form-control m-2 p-1" placeholder="product image "/>
            <input ref={productPrice} className="form-control m-2" placeholder="product Price "/>
            <input ref={productDescription} className="form-control m-2" placeholder="product Description "/>
            <button onClick={updateValues} className="btn btn-success m-2">Submit</button>
        </div>
    </div>


    return<>
    <Home/>
    <h1 className='text-center'>ProductList</h1>
      <div className='container'>
        <table className='table'>
            <thead>
                <th>Sr. No.</th>
                <th>Product Name</th>
                <th>Product Brand</th>
                <th>Product Image</th>
                <th>Product Price</th>
                <th>Delete</th>
                <th>Edit</th>
            </thead>
            {productlist.map((data,index)=><tr key={index}>             
                <td>{index+1}</td>
                <td>{data.title}</td>
                <td>{data.brand}</td>
                <td><img src={data.thumbnail} width='100px' height='80px' alt='product'/></td>
                <td>{data.price} ₹</td>
                <td><button onClick={()=>deleteProduct(index)} className='btn btn-outline-danger'>Delete</button></td>
                <td><button onClick={updateStatus}  className='btn btn-outline-primary'><Link className='text-dark' to='/editProductDetals' onClick={()=>editProductDetail({...data})}>Edit</Link></button></td>
            </tr>)}
        </table>
      </div>
     ({status && <EditProductDetails allProduct = {allProduct}/>})
    </>
}
