import axios from 'axios';
import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import EditProductDetails from './editProductDetails';

export default function Productlist() {
    const [productlist, setProducts] = useState([]);
    const [status, setStatus] = useState(false);
    const [state, setState] = useState({});
    const [stateIndex, setIndex] = useState(null);
    const productName = useRef(null);
    const productBrand = useRef(null);
    const productImg = useRef(null);
    const productPrice = useRef(null);
    const productDescription = useRef(null);

    useEffect(() => {
        axios.get("https://dummyjson.com/products")
            .then(response => {
                setProducts(response.data.products);
            })
            .catch(err => {
                console.log(err);
            });
    }, []);

    const updateStatus = () => {
        const index = stateIndex;
        const arr = [...productlist];
        arr[index].title = productName.current.value;
        arr[index].brand = productBrand.current.value;
        // For file uploads, you need to handle it differently
        // Update the state with the file or the file URL
        // Assuming you want to store the file object
        arr[index].thumbnail = productImg.current.files[0];
        console.log("img "+productImg.current)
        // alert(productImg.current.files[0])
        arr[index].price = parseFloat(productPrice.current.value);
        arr[index].description = productDescription.current.value;

        setProducts(arr);

        const editedForm = document.getElementById("editedForm");
        editedForm.style.display = 'none';
        const Table = document.getElementById("table");
        Table.style.display = 'block';

        setStatus(true);
    };

    const deleteProduct = (index) => {
        const arr = [...productlist];
        arr.splice(index, 1);
        setProducts(arr);
    };

    const editProductDetail = (data, index) => {
        setState(data);
        setIndex(index);

        const editedForm = document.getElementById("editedForm");
        editedForm.style.display = 'block';
        const Table = document.getElementById("table");
        Table.style.display = 'none';
    };

    return (
        <>
            <div className='container-fluid' id='table'>
                <h1 className='text-center'>ProductList</h1>
                <div className='container'>
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>Sr. No.</th>
                                <th>Product Name</th>
                                <th>Product Brand</th>
                                <th>Product Image</th>
                                <th>Product Price</th>
                                <th>Delete</th>
                                <th>Edit</th>
                            </tr>
                        </thead>
                        <tbody>
                            {productlist.map((data, index) => (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{data.title}</td>
                                    <td>{data.brand}</td>
                                    <td>
                                        <img src={data.thumbnail} width='100px' height='80px' alt='product' />
                                    </td>
                                    <td>{data.price} â‚¹</td>
                                    <td>
                                        <button onClick={() => deleteProduct(index)} className='btn btn-outline-danger'>Delete</button>
                                    </td>
                                    <td>
                                        <button onClick={() => editProductDetail(data, index)} className='btn btn-outline-primary'>Edit</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            <div className="container border mt-2" id='editedForm'>
                <h3 className="text-center mt-2">Edit Product Details</h3>
                <div className="form-group p-2">
                    <input ref={productName} className="form-control m-2" placeholder="product name" defaultValue={state.title} />
                    <input ref={productBrand} className="form-control m-2" placeholder="product brand " defaultValue={state.brand} />
                    <input ref={productImg} type="file" className="form-control m-2 p-1" placeholder="product image " />
                    <input ref={productPrice} className="form-control m-2" placeholder="product Price " defaultValue={state.price} />
                    <input ref={productDescription} className="form-control m-2" placeholder="product Description " defaultValue={state.description} />
                    <button onClick={updateStatus} className="btn btn-success m-2">Submit</button>
                </div>
            </div>

            {status && <EditProductDetails allProduct={productlist} />}
        </>
    );
}
